import React, { useState } from 'react';

function App() {
  const [output, setOutput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const callLambdaFunction = async () => {
    setLoading(true);
    setError('');
    try {
      // Replace this URL with your actual Lambda function URL
      const lambdaUrl = "https://zmv8ad1dmk.execute-api.us-west-2.amazonaws.com/newstage";

      const response = await fetch(lambdaUrl, {
        method: 'POST', // Change this method if your Lambda expects a POST request
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const result = await response.body();
        setOutput(result);
      } else {
        setError('Failed to fetch data from Lambda');
      }
    } catch (err) {
      setError('Error calling Lambda function');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Lambda Text Extraction</h1>
      <button onClick={callLambdaFunction} disabled={loading}>
        {loading ? 'Loading...' : 'Call Lambda'}
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h3>Output:</h3>
      <pre>{output}</pre>
    </div>
  );
}

export default App;
